mapper.select:
SELECT COUNT(*) FROM aa_organ org, aa_operator o,aa_operator_role b,aa_role c WHERE org.path LIKE CONCAT('%',#{pathLike},'%') AND org.organ_id = o.organ_id and o.op_id=b.op_id and b.role_id=c.role_id AND o.is_delete = 0
AND o.login_name =#{loginName}
AND o.real_name LIKE CONCAT('%',#{realName},'%')
AND c.role_name =#{roleName}

SELECT , org.organ_name AS organName FROM aa_organ org, aa_operator o,aa_operator_role b,aa_role c WHERE org.path LIKE CONCAT('%',#{pathLike},'%') AND org.organ_id = o.organ_id and o.op_id=b.op_id and b.role_id=c.role_id AND o.is_delete = 0
AND o.login_name =#{loginName}
AND o.real_name LIKE CONCAT('%',#{realName},'%')
AND c.role_name =#{roleName}
ORDER BY o.add_time
LIMIT #{offset}, #{pagesize}

    SELECT DISTINCT
	  m.auth_tag
	FROM aa_operator_role opr,
	  aa_role_menu rm,
	  aa_menu m
	WHERE opr.op_id = #{opId}
	    AND opr.role_id = rm.role_id
	    AND rm.menu_id = m.menu_id
	    AND m.menu_type = #{menuTypeZd}
	    AND m.state = #{menuStateZd}
