mapper.select:
select * from yy_activity
where is_delete = 0

    and product_id =#{productId}

order by id desc

    LIMIT #{offset}, #{pagesize}

select * from yy_activity where is_delete = 0
and product_id =#{productId}
order by id desc

SELECT * FROM yy_activity_prize  where is_delete = 0

    and coupon_id = #{couponId,jdbcType=INTEGER}

and prize_type in('other','bzj') order by create_time  desc

SELECT a.* FROM yy_activity_prize a left join lsd_coupon c on a.coupon_id=c.id where a.is_delete = 0 and c.is_delete=0 and c.quota > c.quota_send
and a.coupon_id = #{couponId,jdbcType=INTEGER}
and ((c.expiry_type = 0) or ( c.expiry_type = 1 and c.gmt_end > NOW() and c.gmt_start < NOW())) order by a.create_time desc
