mapper.select:
select a.*,b.coupon_name as ,b.amount from yy_coupon_user a left join yy_coupon b on a.coupon_id=b.id
where a.is_delete = 0 and a.user_id = #{userId} and a.state = #{state}
order by ${orderName} desc

select a.*,b.coupon_name,b.amount from yy_coupon_user a left join yy_coupon b on a.coupon_id=b.id  where a.is_delete = 0 and a.product_id=#{productId}

  and b.coupon_name=#{couponName}


  and a.user_id=#{userId}


  and a.used_time >=#{useStartTime}


  and a.used_time <=#{useEndTime}


  and a.add_time >=#{receiveStartTime}


  and a.add_time <=#{receiveEndTime}


  and a.coupon_id in (#{couponIds})


  and a.used_time is null and  a.end_time >now()


  and a.used_time is not null


  and a.used_time is  null and a.end_time < now()

order by a.id desc

  LIMIT #{offset}, #{pagesize}

select count(*) from yy_coupon_user a left join yy_coupon b on a.coupon_id=b.id  where a.is_delete = 0 and a.product_id=#{productId}

  and b.coupon_name=#{couponName}


  and a.user_id=#{userId}


  and a.used_time >=#{useStartTime}


  and a.used_time <=#{useEndTime}


  and a.add_time >=#{receiveStartTime}


  and a.add_time <=#{receiveEndTime}


  and a.coupon_id in (#{couponIds})


  and a.used_time is null and  a.end_time >now()


  and a.used_time is not null


  and a.used_time is  null and a.end_time < now()

order by a.id desc

select count(user_id) as count,user_id from yy_coupon_user where product_id=#{productId} and coupon_id=#{couponId,jdbcType=INTEGER} and user_id in
#{userId}
GROUP BY user_id
